# Telegram Watcher for Ren
# Polls Telegram bot for new messages, pastes them into Cowork, hits Enter
# Usage: powershell -ExecutionPolicy Bypass -File telegram_watcher.ps1

Add-Type -AssemblyName System.Windows.Forms

$token = "8591631324:AAGKVbiGgnrZ-XdMc5WdcA1QbjCC05eH9fY"
$stateFile = "$PSScriptRoot\telegram_watcher_state.json"
$pollInterval = 60  # seconds between checks

# Load or init state
function Get-State {
    if (Test-Path $stateFile) {
        return Get-Content $stateFile | ConvertFrom-Json
    }
    return @{ last_update_id = 0 }
}

function Save-State($state) {
    $state | ConvertTo-Json | Set-Content $stateFile
}

function Check-Telegram($lastId) {
    $uri = "https://api.telegram.org/bot$token/getUpdates?timeout=5"
    if ($lastId -gt 0) {
        $uri += "&offset=$($lastId + 1)"
    }
    try {
        $response = Invoke-RestMethod -Uri $uri -Method Get -TimeoutSec 15
        return $response
    } catch {
        Write-Host "[ERROR] Telegram API call failed: $_"
        return $null
    }
}

function Send-ToCowork($text) {
    # Find the Claude/Cowork window
    $proc = Get-Process | Where-Object { $_.ProcessName -eq 'claude' -and $_.MainWindowTitle } | Select-Object -First 1

    if (-not $proc) {
        Write-Host "[WARN] Claude window not found. Skipping."
        return $false
    }

    # Bring window to front
    $sig = '[DllImport("user32.dll")] public static extern bool SetForegroundWindow(IntPtr hWnd);'
    $type = Add-Type -MemberDefinition $sig -Name 'Win32SetForeground' -Namespace 'Win32Functions' -PassThru -ErrorAction SilentlyContinue
    if (-not $type) {
        $type = [Win32Functions.Win32SetForeground]
    }
    $type::SetForegroundWindow($proc.MainWindowHandle) | Out-Null
    Start-Sleep -Milliseconds 500

    # Copy message to clipboard and paste
    [System.Windows.Forms.Clipboard]::SetText($text)
    Start-Sleep -Milliseconds 200
    [System.Windows.Forms.SendKeys]::SendWait("^v")
    Start-Sleep -Milliseconds 300

    # Hit Enter to send
    [System.Windows.Forms.SendKeys]::SendWait("{ENTER}")

    Write-Host "[SENT] Delivered message to Cowork"
    return $true
}

# Main loop
Write-Host "=== Ren Telegram Watcher ==="
Write-Host "Polling every $pollInterval seconds..."
Write-Host "Press Ctrl+C to stop."
Write-Host ""

while ($true) {
    $state = Get-State
    $response = Check-Telegram $state.last_update_id

    if ($response -and $response.ok -and $response.result.Count -gt 0) {
        foreach ($update in $response.result) {
            $msg = $update.message
            if ($msg -and $msg.text -and $msg.text -ne "/start") {
                $sender = $msg.from.first_name
                $text = $msg.text
                $timestamp = (Get-Date).ToString("HH:mm:ss")

                Write-Host "[$timestamp] New message from $sender`: $text"

                # Format the prompt for Cowork
                $prompt = "[Telegram from Jason]: $text"

                # Deliver to Cowork
                $delivered = Send-ToCowork $prompt

                if (-not $delivered) {
                    Write-Host "[WARN] Could not deliver. Will retry next cycle."
                }
            }

            # Update offset
            if ($update.update_id -ge $state.last_update_id) {
                $state.last_update_id = $update.update_id
            }
        }
        Save-State $state
    } else {
        $timestamp = (Get-Date).ToString("HH:mm:ss")
        Write-Host "[$timestamp] No new messages."
    }

    Start-Sleep -Seconds $pollInterval
}
