#!/usr/bin/env python3
"""
Telegram Outbound for Ren
Sends a message from Ren to Jason via the Telegram bot.
Used by scheduled tasks for Ren-initiated communication.

Usage:
  python3 telegram_outbound.py "message text here"
"""

import sys
import json
import requests
from pathlib import Path

TOKEN_PATH = Path("/sessions/bold-compassionate-ride/.ren/telegram_token.txt")
CHAT_ID = 8139923446  # Jason's Telegram chat ID

def send(text):
    token = TOKEN_PATH.read_text().strip()
    url = f"https://api.telegram.org/bot{token}/sendMessage"

    # Try with markdown first
    r = requests.post(url, json={
        "chat_id": CHAT_ID,
        "text": text,
        "parse_mode": "Markdown"
    })

    if not r.json().get("ok"):
        # Fallback to plain text
        r = requests.post(url, json={
            "chat_id": CHAT_ID,
            "text": text
        })

    return r.json().get("ok", False)

if __name__ == "__main__":
    if len(sys.argv) < 2:
        print("Usage: telegram_outbound.py 'message'")
        sys.exit(1)

    message = " ".join(sys.argv[1:])
    if send(message):
        print(f"Sent to Jason: {message[:80]}...")
    else:
        print("Failed to send.")

